const sidebar=document.getElementById("sidebar"),openBtn=document.getElementById("open-btn"),closeBtn=document.getElementById("close-btn");function openSidebar(){sidebar.classList.add("show"),openBtn.classList.add("hidden"),closeBtn.classList.remove("hidden")}function closeSidebar(){sidebar.classList.remove("show"),openBtn.classList.remove("hidden"),closeBtn.classList.add("hidden")}openBtn.addEventListener("click",openSidebar),closeBtn.addEventListener("click",closeSidebar);let currentSlide=0;const slides=document.querySelectorAll(".hero-slide");function showSlide(e){slides.forEach(e=>{e.classList.remove("active")}),slides[e].classList.add("active")}function openModal(e){let t=document.getElementById("imageModal"),s=document.getElementById("modalImg");t.style.display="flex",s.src=e.src}function closeModal(){let e=document.getElementById("imageModal");e.style.display="none"}setInterval(()=>{showSlide(currentSlide=(currentSlide+1)%slides.length)},5e3);const page1=document.querySelector(".page-1"),page2=document.querySelector(".page-2"),flipLeft=document.querySelector(".flip-left"),flipRight=document.querySelector(".flip-right");flipRight.addEventListener("click",()=>{page1.classList.add("hidden"),page2.classList.remove("hidden"),flipRight.classList.add("hidden"),flipLeft.classList.remove("hidden")}),flipLeft.addEventListener("click",()=>{page1.classList.remove("hidden"),page2.classList.add("hidden"),flipRight.classList.remove("hidden"),flipLeft.classList.add("hidden")});let currentIndex=0;const reviews=document.querySelector(".reviews"),reviewCount=document.querySelectorAll(".review").length;function showNextReview(){++currentIndex>=reviewCount&&(currentIndex=0);let e=-(100*currentIndex);reviews.style.transform=`translateX(${e}%)`}setInterval(()=>{showNextReview()},3e3);const faqItems=document.querySelectorAll(".faq-item");function closeOtherFAQs(e){faqItems.forEach(t=>{if(t!==e&&t.classList.contains("active")){t.classList.remove("active");let s=t.querySelector(".faq-answer");s.style.maxHeight="0",s.style.opacity="0"}})}faqItems.forEach(e=>{let t=e.querySelector(".faq-question"),s=e.querySelector(".faq-answer");t.addEventListener("click",()=>{let t=e.classList.toggle("active");s.style.maxHeight=t?s.scrollHeight+"px":"0",s.style.opacity=t?"1":"0",t&&closeOtherFAQs(e)})});